pragma solidity ^0.8.24;

import "@fhevm/solidity/lib/FHE.sol";
import {SepoliaConfig} from "@fhevm/solidity/config/ZamaConfig.sol";
import "../IDecryptionCallbacks.sol";
import "../../core/EncryptedHelper.sol";
import "../../storage/FundraisingStorage.sol";
import "../../storage/ShareVaultStorage.sol";
import "../../struct/CommonStruct.sol";
import "../../struct/FundraisingStruct.sol";
import "../../struct/ShareVaultStruct.sol";

contract DecryptionCallbacks is IDecryptionCallbacks, FundraisingStorage, ShareVaultStorage {

    function callbackDecryptMyContribution(
        uint256 requestId,
        bytes memory cleartexts,
        bytes memory decryptionProof
    ) external override {
        FHE.checkSignatures(requestId, cleartexts, decryptionProof);

        uint64 contributedAmount = EncryptedHelper.decodeUserContribution(
            cleartexts
        );
        FundraisingStruct.DecryptUserContributionRequest memory request = decryptMyContributionRequest[requestId];
        decryptedContributions[request.campaignId][request.userAddress] = CommonStruct.Uint64ResultWithExp({
            data: contributedAmount,
            exp: block.timestamp + cacheTimeout
        });

        delete decryptMyContributionRequest[requestId];

        decryptMyContributionStatus[request.campaignId][
            request.userAddress
        ] = CommonStruct.DecryptStatus.DECRYPTED;
    }

    function callbackDecryptTotalRaised(
        uint256 requestId,
        bytes memory cleartexts,
        bytes memory decryptionProof
    ) external override {
        FHE.checkSignatures(requestId, cleartexts, decryptionProof);

        uint16 campaignId = decryptTotalRaisedRequest[requestId];

        uint64 totalRaised = EncryptedHelper.decodeTotalRaised(
            cleartexts
        );
        decryptedTotalRaised[campaignId] = CommonStruct.Uint64ResultWithExp({
            data: totalRaised,
            exp: block.timestamp + cacheTimeout
        });

        delete decryptTotalRaisedRequest[requestId];
        decryptTotalRaisedStatus[campaignId] = CommonStruct.DecryptStatus.DECRYPTED;
    }

    function callbackDecryptAvailableBalance(
        uint256 requestId,
        bytes memory cleartexts,
        bytes memory decryptionProof
    ) external override {
        FHE.checkSignatures(requestId, cleartexts, decryptionProof);

        uint64 availableAmount = EncryptedHelper.decodeAvailableBalance(cleartexts);

        ShareVaultStruct.WithdrawalRequest memory request = withdrawalRequests[
            requestId
        ];

        decryptedAvailableBalance[request.userAddress] = CommonStruct
            .Uint64ResultWithExp({
                data: availableAmount,
                exp: block.timestamp + CACHE_TIMEOUT
            });

        delete withdrawalRequests[requestId];

        availableBalanceStatus[request.userAddress] = CommonStruct
            .DecryptStatus
            .DECRYPTED;
    }
}","keccak256":"0xd749ba8b21469bf4b80f58784b4334c79c1d3982632ea4eb49cdcf91a959c2c1","license":"MIT"},"contracts/storage/FundraisingStorage.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "../struct/FundraisingStruct.sol";
import "../struct/CommonStruct.sol";

abstract contract FundraisingStorage {
    uint256 cacheTimeout = 600; // 10 mins cache

    mapping(uint16 => FundraisingStruct.Campaign) public campaigns;

    mapping(uint16 => mapping(address => bool)) public hasClaimed;

    mapping(uint16 => mapping(address => euint64)) internal encryptedContributions;
    mapping(uint16 => mapping(address => CommonStruct.Uint64ResultWithExp)) internal decryptedContributions;

    mapping(uint16 => CommonStruct.Uint64ResultWithExp) internal decryptedTotalRaised;
    mapping(uint256 => uint16) internal decryptTotalRaisedRequest;
    mapping(uint16 => CommonStruct.DecryptStatus) internal decryptTotalRaisedStatus;

    mapping(uint256 => FundraisingStruct.DecryptUserContributionRequest) internal decryptMyContributionRequest;
    mapping(uint16 => mapping(address => CommonStruct.DecryptStatus)) internal decryptMyContributionStatus;

    uint16 public campaignCount;

    // Mapping to track contributors per campaign
    mapping(uint16 => address[]) internal campaignContributors;
    mapping(uint16 => mapping(address => bool)) internal hasContributed;

    // Token claim tracking
    mapping(uint16 => mapping(address => bool)) public hasClaimedTokens;
}","keccak256":"0xf44aeb9ee5da28b064e2031dd2b91b3d94f52f90a6f90bb6a50a47dca368e5b6","license":"MIT"},"contracts/storage/ShareVaultStorage.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "../struct/ShareVaultStruct.sol";
import "../struct/CommonStruct.sol";

abstract contract ShareVaultStorage {
    // User balances (encrypted)
    mapping(address => euint64) internal encryptedBalances;

    // Locked amounts per user per campaign (encrypted)
    mapping(address => mapping(uint16 => euint64)) internal lockedAmounts;

    // Total locked per user (encrypted)
    mapping(address => euint64) internal totalLocked;

    // Decryption status and cached available balance
    mapping(address => CommonStruct.DecryptStatus)
        internal availableBalanceStatus;
    mapping(address => CommonStruct.Uint64ResultWithExp)
        internal decryptedAvailableBalance;
    mapping(uint256 => ShareVaultStruct.WithdrawalRequest)
        internal withdrawalRequests;

    // Campaign contract address (authorized to lock/unlock)
    address public campaignContract;

    // Cache timeout (10 minutes)
    uint256 public constant CACHE_TIMEOUT = 600;
}","keccak256":"0xb96846246d5605f9ec72e594fac01795bc5dcb5c9ac0f8a49676ade64e3b2803","license":"MIT"},"contracts/struct/CommonStruct.sol":{"content":"// SPDX-License-Identifier: MIT
